    }

  render() {
      return (
        <div>
            <div className='title'>線上簽到系統</div>
            <Grid container spacing={3}>
                <Grid item xs={2}  className="day">
                    <div className="button-name">前{2}天</div>
                    <button onClick={this.changeImageHandler}>
                        <img src={this.imagesPath.unclicked} className="check-in" onClick={this.toggleImage} />
                    </button>
                </Grid>

                <Grid item xs={2}  className="day">
                    <div>前{1}天</div>
                        <button type="button">
                            <img src="/image/unclicked.png" className="check-in" />
                        </button>
                </Grid>

                <Grid item xs={2}  className="day">
                    <div>今天</div>
                    <button type="button">
                        <img src="/image/unclicked.png" className="check-in" />
                    </button>
                </Grid>

            </Grid>

            <div className="total-ch">
                簽到總和：<div>{ this.state.count }</div>
            </div>

        </div>
      );
  }
}

export default Checklist;
