import React, {Component} from 'react';
import "../sass/Checklist.scss";
import { Grid } from '@material-ui/core';
class Checklist extends Component {



    state = {
        count: 0,
        day: 1,
        status: "unclicked"
    };

    imagesPath = {
        unclicked: "/image/question.png",
        clicked: "/image/check.png",
        missed: "/image/remove.png"
    }

    toggleImage = () => {
        if(this.state.status === "unclicked") {
            this.setState(state => ({ status: "clicked" }))
        }
      }



  render() {
      return (
        <div>
            <div>線上簽到系統</div>
            <Grid container spacing={3}>
                <Grid item xs={2}  className="day">
                    <div className="button-name">第{this.state.day}天</div>
                    <button type="button">
                        <img src={ 'this.imagesPath.${this.state.status}' } className="check-in" onClick={this.toggleImage} />
                    </button>
                </Grid>

                <Grid item xs={2}  className="day">
                    <div>第{this.state.day+1}天</div>
                        <button type="button">
                            <img src="/image/question.png" className="check-in" />
                        </button>
                </Grid>

                <Grid item xs={2}  className="day">
                    <div>第{this.state.day+2}天</div>
                    <button type="button">
                        <img src="/image/question.png" className="check-in" />
                    </button>
                </Grid>

            </Grid>

            <div>
                簽到總和：<div>{ this.state.count }</div>
            </div>

        </div>
      );
  }
}

export default Checklist;
