import React, { Component} from "react";
import { MDBContainer, MDBRow, MDBCol, MDBBtn } from "mdbreact";
import serverip from "./config/serverip";
import axios from "axios";
import setJWTToken from "./setJWTToken";
import jwt_decode from "jwt-decode";
class Signin extends Component {

  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: ""
    }
  }

  // componentDidMount() {
  //   if (this.props.security.validToken) {
  //     this.props.history.push("/");
  //   }
  // }

  // componentWillReceiveProps(nextProps) {
  //   if (nextProps.security.validToken) {
  //     this.props.history.push("/");
  //   }

  //   if (nextProps.errors) {
  //     this.setState({ errors: nextProps.errors });
  //   }
  // }



  // 改變輸入資料到state
  handleInputChange = event => {
    const { value, email } = event.target;
    this.setState({
      [email]: value
    });
  };


  // 提交登入資料
  onSubmit = event => {
    const LoginRequest = {
      email: this.state.email,
      password: this.state.password
    };
  };

  login = LoginRequest => async dispatch => {
    try {
      // post => Login Request
      const res = await axios.post("/login", LoginRequest);

      // extract token from res.data
      const { token } = res.data;

      // store the token in the localStorage
      localStorage.setItem("jwtToken", token);

      // set our token in header ***
      setJWTToken(token);

      // decode token on React
      const decoded = jwt_decode(token);

      // dispatch to our securityReducer
      dispatch({
        type: 1,
        payload: decoded
      });
    } catch (err) {
      dispatch({
        type: -1,
        payload: err.response.data
      });
    }
  };



  render() {
    return (
      <MDBContainer className="h630">
        <MDBRow>
          <MDBCol md="4">
            <form onSubmit={this.onSubmit}>
              <p className="h1 text-center mb-4">Sign in</p>
              <label htmlFor="defaultFormLoginNameEx" className="grey-text">
                email
              </label>
              <input
                type="text"
                name="email"
                id="defaultFormLoginNameEx"
                className="form-control"
                value={this.state.email}
                onChange={this.handleInputChange}
                required
              />
              <br />
              <label htmlFor="defaultFormLoginPasswordEx" className="grey-text">
                password
              </label>
              <input
                type="text"
                name="password"
                id="defaultFormLoginPasswordEx"
                className="form-control"
                value={this.state.password}
                onChange={this.handleInputChange}
                required
              />
              <div className="text-center mt-4">
                <MDBBtn color="indigo" type="submit">
                  Login
                </MDBBtn>
              </div>
            </form>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    );
  }
}

export default Signin;
