import React, { Component} from "react";
import { MDBContainer, MDBRow, MDBCol, MDBBtn } from "mdbreact";
import serverip from "./config/serverip";
class Signin extends Component {

  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: ""
    }
    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }

  componentDidMount() {
    if (this.props.security.validToken) {
      this.props.history.push("/");
    }
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.security.validToken) {
      this.props.history.push("/");
    }

    if (nextProps.errors) {
      this.setState({ errors: nextProps.errors });
    }
  }



  // 改變輸入資料到state
  handleInputChange = event => {
    const { value, email } = event.target;
    this.setState({
      [email]: value
    });
  };


  // 提交登入資料
  onSubmit = event => {
    const LoginRequest = {
      email: this.state.email,
      password: this.state.password
    };

    try {
      // post => Login Request
      const res = axios.post("/login", LoginRequest);

      // extract token from res.data
      const { token } = res.data;

      // store the token in the localStorage
      localStorage.setItem("jwtToken", token);

      // set our token in header ***
      setJWTToken(token);

      // decode token on React
      const decoded = jwt_decode(token);

      // dispatch to our securityReducer
      dispatch({
        type: SET_CURRENT_USER,
        payload: decoded
      });
    } catch (err) {
      dispatch({
        type: GET_ERRORS,
        payload: err.response.data
      });
    }

    /*
    try {
      // post => Login Request
      const res = await axios.post("/api/users/login", LoginRequest);
      // extract token from res.data
      const { token } = res.data;
      // store the token in the localStorage
      localStorage.setItem("jwtToken", token);
      // set our token in header ***
      setJWTToken(token);
      // decode token on React
      const decoded = jwt_decode(token);
      // dispatch to our securityReducer
      dispatch({
        type: SET_CURRENT_USER,
        payload: decoded
      });
    } catch (err) {
      dispatch({
        type: GET_ERRORS,
        payload: err.response.data
      });
    }
    */
    // event.preventDefault();
    // let url = serverip;

    // console.log("url:"+url)

    // fetch(url + "/users", {
    //   method: "POST",
    //   body: JSON.stringify(this.state),
    //   headers: {
    //     "Content-Type": "application/json"
    //   },
    //   credentials: 'include',
    // })
    //   .then(res => {
    //     if (res.status === 200) {
    //       this.props.history.push("/");
    //     } else {
    //       const error = new Error(res.error);
    //       throw error;
    //     }
    //   })
    //   .catch(err => {
    //     console.error("error");
    //     alert("Error logging in, please try again");
    //   });
  };

  render() {
    return (
      <MDBContainer className="h630">
        <MDBRow>
          <MDBCol md="4">
            <form onSubmit={this.onSubmit}>
              <p className="h1 text-center mb-4">Sign in</p>
              <label htmlFor="defaultFormLoginNameEx" className="grey-text">
                email
              </label>
              <input
                type="email"
                name="email"
                id="defaultFormLoginNameEx"
                className="form-control"
                value={this.state.email}
                onChange={this.handleInputChange}
                required
              />
              <br />
              <label htmlFor="defaultFormLoginPasswordEx" className="grey-text">
                password
              </label>
              <input
                type="password"
                name="password"
                id="defaultFormLoginPasswordEx"
                className="form-control"
                value={this.state.password}
                onChange={this.handleInputChange}
                required
              />
              <div className="text-center mt-4">
                <MDBBtn color="indigo" type="submit">
                  Login
                </MDBBtn>
              </div>
            </form>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    );
  }
}

export default Signin;
